const deadMole = document.getElementById('dead'); const lostMole = document.getElementById('lost'); const getHoleClass = function (e) { return document.getElementById('hole'.concat(e)); }; const _loop = function () { const e = document.getElementById(getHoleClass(i).id); e.onclick = function () { e.className == 'hole hole_has-mole' ? (deadMole.textContent = Number(deadMole.textContent) + 1, deadMole.textContent == 10 && (alert('Вы победили!'), deadMole.textContent = 0, lostMole.textContent = 0)) : (lostMole.textContent = Number(lostMole.textContent) + 1, lostMole.textContent == 5 && (alert('Вы проиграли :-('), deadMole.textContent = 0, lostMole.textContent = 0)); }; }; for (i = 1; i <= 9; i++)_loop();
